import './App.css';
import Header from './components/Header/Header';
import useForecast from './useForecast';
import Forecast from './components/Forecast/Forecast';
import Loader from './components/Loader/Loader';
import BienveunMessage from './components/BienveunMessage/BienveunMessage';
import Error from './components/Error/Error';
import Cookies from 'js-cookie'
// import bgGlobal from "./bgGlobal.jpg"

function App() {

  const favorieCookie = Cookies.get('favorie');

  const {submitRequest,forecast,isError,isLoading} = useForecast();

  if( favorieCookie ){

    submitRequest(favorieCookie);
    return (
      <div className="App">
        <Header submitRequest={submitRequest}/>
        {!forecast && (
              <div>
                {!isLoading && !isError && <BienveunMessage/>}
                {isError && <Error/> }
                {isLoading && <Loader/> }
              </div>
          )
        }
        { forecast && (
          <>
           {!isLoading && !isError && forecast && 
            <> <Forecast forecast={forecast}/> </>
          }
          { (isLoading || isError) &&
              <>
                  {isError && <Error/> }
                  {isLoading && <Loader/> }
              </>
          }
          </>
        )}


      </div>
    );
  }
  return;
}

export default App;
