import React,{useRef,useEffect,useState,useLayoutEffect} from 'react'
import { Container } from '@material-ui/core';
import CircleProgress from "../CircleProgress";
import { WiDaySunny } from "react-icons/wi";


import ReactCustomizableProgressbar from 'react-customizable-progressbar';
const BienveunMessage = () => {

    const divRef = useRef();

    const [width, setWidth] = useState();
    const [height, setHeight] = useState();

    const getDivSize = () => {

        const newWidth = divRef.current.clientWidth;
        setWidth(newWidth);

        const newHeight = divRef.current.clientHeight;
        setHeight(newHeight);

      };

      useLayoutEffect(() => {
        getDivSize();
      }, []);

      useEffect(() => {
        window.addEventListener("resize", getDivSize);
        return () => {
            window.removeEventListener('resize', getDivSize);
          };
      }, [divRef]);

      console.log(`width : ${width} // height : ${height}`);
    //   console.log(`);
      const icon = <WiDaySunny color={'red'}/>;
    return (
        <Container className="p-5" ref={divRef}>
            <h1>Bienvenue</h1>
            <p>Selectionner une ville dans la barre de recherche</p>

           <ReactCustomizableProgressbar 
                  radius={100}
                  progress={25}
                  rotate={180}
                  fillColor="#f2f2f2"
                  strokeWidth={3}
                  strokeColor="#ffd100"
                  trackStrokeWidth={3}
                  pointerRadius={17}
                  pointerStrokeWidth={3}
                  pointerStrokeColor="#ffd100"
                  children={<WiDaySunny size={30} color={'#fcd100'} 
                  style={{transformOrigin: `50% 50%`, transform: `rotate('90deg') translate(20px)`, transition: `all 0.3s ease 0s`}}/>}
          />

          {/* <CircleProgress size="250" strokeWidth={20}/> */}

          <svg width="200" height="200">
            <g transform="rotate(-90 100 100)">
              <circle r="70" cx="100" cy="100" fill="transparent" stroke="lightgrey" stroke-width="2rem" stroke-dasharray="439.8" stroke-dashoffset="0"></circle>
              <circle r="70" cx="100" cy="100" fill="transparent" stroke="blue" stroke-width="2rem" stroke-dasharray="439.8" stroke-dashoffset="66"> 
              </circle>
              <circle cx="200" cy="50%" r="16" fill="white" stroke="indianred" stroke-width="3" class="RCP__pointer" ></circle>

            </g>
            <text x="50%" y="50%" dominant-baseline="central" text-anchor="middle">85%</text>
          </svg>
        </Container>

    )
}

export default BienveunMessage
