import { createGlobalStyle } from 'styled-components';


export const GlobalStyles = createGlobalStyle `
.App {
    text-align: center;
    display: flex;
    min-height: 100vh;

    justify-content: start;
    justify-items: center;
    flex-direction: column;
    background: #181f28;
    color:whitesmoke !important;
     background-size: cover;
  }
`
;
