import React, { useState } from 'react'
import {Container,Row,Col,Form} from 'react-bootstrap';
import styles from './CurrentForecast.module.css';
import {GradeOutlined, Grade} from '@material-ui/icons';

const CurrentForecast = ({location,temperature,weatherIcon,weatherDescription,country,weekday,date}) => {
    const newWeatherIcon = weatherIcon.replace("'","");
    const [favorie, setFavorie ] = useState('')
    const [checked, setChecked ] = useState('')

    const handleFavorie = (e) =>{
        console.log(favorie);
        console.log(favorie);
        if(!favorie){
            setFavorie('true');
            setChecked(true);
        }else{
            setFavorie('false');
            setChecked(false)
        }
    }
    return (
        <Container className={`container ${styles.currentContent}`}>
        <Row className="justify-content-md-center">
            <Col xs lg="5">
                <Row>
                    <Col className={`${styles.countryNameContent}`}>
                    <Form className="d-flex justify-content-center">
                        <GradeOutlined/>
                        <Form.Check 
                                type="switch"
                                id="custom-switch"
                                label=""
                                checked={checked}
                                favorie={favorie}
                                onChange={e => handleFavorie(e.target.value)}
                        />
                        <Grade/>
                    </Form>
                    <span> {location} / <b>{country}</b>  </span>
                    </Col>
                </Row>
                <Row>
                    <Col className={`${styles.dayName}`}>
                    <span> {weekday} - {date}</span>
                    </Col>
                </Row>
                <Row className="d-flex justify-content-center ">
                    <Col className={`  d-flex justify-content-center pt-3 pb-2`}>
                    <div className={`${styles.wetherIcon}  d-flex justify-content-center align-items-center p-0`}
                        ><img src={newWeatherIcon}/>
                        </div>
                    <div className={`${styles.temp}  d-flex align-items-center p-0`} ><span >{temperature}&deg;</span></div>

                    </Col>

                </Row>
                <Row>
                    <Col >
                    <span> {weatherDescription} </span>
                    </Col>
                </Row>
                <Row>
                    <Col className="bg-success">pays</Col>
                    <Col className="bg-secondary">pays</Col>
                    <Col className="bg-success">pays</Col>
                </Row>
            </Col>
        </Row>
        </Container>
    )
}

export default CurrentForecast
