const divRef = createRef();
const [dimensions, setDimensions] = useState({ width: 1, height: 2 });

const useRefDimensions = (divRef) => {
    // const [dimensions, setDimensions] = useState({ width: 1, height: 2 })

    const newWidth = divRef.current.clientWidth;
    //     setWidth(newWidth);

    //     const newHeight = divRef.current.clientHeight;
    //     setHeight(newHeight);

    return dimensions
  }

//   const dimensions = useRefDimensions(divRef)

  useEffect(() => {
    if (divRef.current) {
      const { current } = divRef
      const boundingRect = current.getBoundingClientRect()
      const { width, height } = boundingRect
      setDimensions({ width: Math.round(width), height: Math.round(height) });
    }
  }, [])

     useEffect(() => {
        window.addEventListener("resize", useRefDimensions);
      }, [divRef]);

  console.log(dimensions);

    return (
        <Container className="p-5 bg-primary" ref={divRef}>
            <h1>Bienvenue</h1>
            <p>Selectionner une ville dans la barre de recherche</p>
        </Container>
    )
}

export default BienveunMessage
