import { Container } from '@material-ui/core';

const BienveunMessage = () => {

    const divRef = useRef();

    const [width, setWidth] = useState();
    const [height, setHeight] = useState();

    const getDivSize = () => {

        const newWidth = divRef.current.clientWidth;
        setWidth(newWidth);

        const newHeight = refs.divRef.clientHeight;
        setHeight(newHeight);

      };

      useLayoutEffect(() => {
        getDivSize();
      }, []);

      useEffect(() => {
        window.addEventListener("resize", getDivSize);
      }, [divRef]);

      console.log(`width : ${width}`);
      console.log(`height : ${height}`);

    return (
        <Container className="p-5 bg-primary" ref={divRef}>
            <h1>Bienvenue</h1>
            <p>Selectionner une ville dans la barre de recherche</p>
        </Container>
    )
}

