import useForecast from './useForecast';
import Forecast from './components/Forecast/Forecast';
import Header from './components/Header/Header';
import BienveunMessage from './components/BienveunMessage/BienveunMessage';
import Loader from './components/Loader/Loader';
import Error from './components/Error/Error';
import Footer from './components/Footer/Footer';
import { GlobalStyles } from './components/Theme/Styles/GlobalStyles';
import { ToggleTheme } from './components/Theme/ToggleTheme';
import { Container } from '@material-ui/core';

function App() {

  const {submitRequest,forecast,isError,isLoading} = useForecast();

  return (
    <div  >
      <GlobalStyles/>
      <div className="App">
        <Header submitRequest={submitRequest} />
        <Container>
         <ToggleTheme/>
        </Container>
        <div className="content-app">
          <span></span> {/* empty span */}
          {!forecast && (
                <div>
                  {!isLoading && !isError && <BienveunMessage/>}
                  {isError && <Error/> }
                  {isLoading && <Loader/> }
                </div>
            )
          }
          { forecast && (
            <>
            {!isLoading && !isError && forecast && 
              <> <Forecast forecast={forecast}/> </>
            }
            { (isLoading || isError) &&
                <>
                    {isError && <Error/> }
                    {isLoading && <Loader/> }
                </>
            }
            </>
          )}
          <Footer/>
        </div>
      </div>
    </div>
  );
}

export default App;
