import React,{useRef,useEffect,useState,useLayoutEffect} from 'react'
import { Container } from '@material-ui/core';
import {WiMoonAltNew} from 'react-icons/wi';

import ReactCustomizableProgressbar from 'react-customizable-progressbar';
const BienveunMessage = () => {

        const size  = 500; 
        const progress  = 50;
        const strokeWidth  = 15;
        const circleOneStroke = '#7ea9e1';
        const circleTwoStroke = '#7ea9e1';

        const center = size / 2;
        const radius = size / 2 - strokeWidth / 2;
      const circumference = 2 * Math.PI * radius;

    return (
        <Container className="p-5" >
            <h1>Bienvenue</h1>
            <p>Selectionner une ville dans la barre de recherche</p>


            <svg className="svg" width={size} height={size}>
              <circle
                  className="svg-circle-bg"
                  stroke={circleOneStroke}
                  cx={center}
                  cy={center}
                  r={radius}
                  strokeWidth={strokeWidth}
              />
              <circle
                  className="svg-circle"
                  stroke=''
                  cx={center}
                  cy={center}
                  r={radius}
                  strokeWidth={strokeWidth}
                  strokeDasharray={circumference}
              />

            <circle
                className="svg-circle"
                stroke={circleTwoStroke}
                fill="white"
                cx={0}
                cy={250}
                r={40}
                strokeWidth={strokeWidth/2}
                strokeDasharray={circumference/2}
            >
               <pattern className="svg-circle-text" x={center}  y={center} id="image" x="0%" y="0%" height="200" width="200" viewBox="1 0 58 58">
              <WiMoonAltNew/>
            </pattern>
            </circle>
              {/* <text > */}



              {/* </text> */}
          </svg>

      </Container>

        // style="transform-origin: 50% 50%; transform: rotate(237.6deg) translate(19px); transition: all 0.3s ease 0s;"
    )
}

export default BienveunMessage
